import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import Charts from '../components/Charts'

export default class PosProps extends Component {
    colored = (value)=> {
        if(value>0) {
          return {color:"green",fontSize:"1rem",fontWeight:"600",padding:"0px",textAlign:"center"}
         } else if(value<0) {
            return {color:"rgb(250, 100, 100)",fontSize:"0.9rem",fontWeight:"600",padding:"0px",textAlign:"center"}
          } else {
            return {color: "yellow",fontSize:"1rem",fontWeight:"600",padding:"0px",textAlign:"center"}
          }
        }
    
    render() {
        const roundTo = require('round-to')
        const {ticker, shares, investCost, currentCost, margin, marginPercentage} = this.props.item;
        
        return (
            <tr>
                <th style={styledCell}><button style={btnSymStyle} onClick={()=>{this.props.setChartsTicker(ticker)}}>{ticker}</button></th>
                <td style={styledCell}><div style={styledCell}>{shares}</div></td>
                <td style={styledCell}><div style={styledCell}>{investCost}</div></td>
                <td style={styledCell}><div style={styledCell}>{currentCost}</div></td>
                <td style={styledCell}><div style={this.colored(margin)}>{margin}</div></td>
                <td style={styledCell}><div style={this.colored(marginPercentage)}>{marginPercentage}</div></td>
                <td><button>chart</button></td>
            </tr>
        )
    }
}
const btnSymStyle ={
    background:'transparent',
    border: 'none',
    color: 'skyblue',
    fontSize: '0.7rem'
}

const styledCell = {
    textAlign:"center",
    padding:"0rem"
  }
