import React, {Component, Fragment} from 'react';
import ReactApexChart from 'react-apexcharts';

class AreaChart extends Component {
  constructor(props) {
    super(props);
    this.state = {
    ticker: null,
    render: true,
    chart: null,
    options: {
      chart: {
        zoom: {
          enabled: false
        }
      },
      dataLabels: {
        enabled: false
      },
      stroke: {
        curve: 'straight'
      },
      series: [{
        name: "STOCK ABC",
        data: "no-data" // need to adjust
      }],
      title: {
        text: 'Fundamental Analysis of Stocks',
        align: 'left'
      },
      subtitle: {
        text: 'Price Movements',
        align: 'left'
      },
      labels: "no-label",
      xaxis: {
        type: 'datetime',
      },
      yaxis: {
        opposite: true
      },
      legend: {
        horizontalAlign: 'left'
      }
    
    },
    series: []
  }
  }

  setChartFromIEXResponse = (json, ticker) => {
    const newOptions = this.state.options
    newOptions.xaxis = {'categories': json.map(entry=>entry.date)}
    const stateToSet =
      {
          ticker: ticker,
          render: true,
          options: newOptions,
          'series': [{
            'name': this.state.ticker,
            'data': json.map(entry=>entry.close)
          }]
        
      }
      console.log("setChartFromIEXResponse", stateToSet)
  }

  componentWillMount() {
     console.log("=== ComponentWillMount === ticker", this.props.tickerToLook)
     this.lookUp(this.props.tickerToLook)
  }

  lookUp = (ticker) => {
    this.setState({ticker:ticker}, (state)=>{
    const promise = fetch(`https://cloud.iexapis.com/stable/stock/${ticker}/chart/1m?token=${process.env.REACT_APP_API_PUBLISH}`)
    promise.then(blob=>blob.json()).then(json=>{
      console.log("lookUp JSON",json)
      this.setChartFromIEXResponse(json, ticker)
    })
  })
  }

  render() {
  console.log("RENDER==lookupticker==",this.state.ticker)
  console.log("RENDER==options==",this.state.options)

    let chart = [];
    if (this.state.render) {
      chart = (<ReactApexChart options={this.state.options}
                      series={this.state.series}
                      type="area" height="350"
                />);
    }
    return <Fragment>

      <form>
        <label htmlFor='ticker'>ticker</label>
        <input name='ticker' value={this.state.ticker} onChange={(event)=>{
          this.setState({ticker: event.target.value})
        }}/>
        <br />
        <button onClick={(event)=>{
          event.preventDefault()
          this.lookUp(this.state.ticker)

          
        }}>Look Up</button>
      </form>
      <div>
      {chart}
      </div>
     
    </Fragment>
  }
}

export default AreaChart;
