import React, { Component, Fragment } from 'react'
import apiCall from '../util/apiCall';
import '../styles/style.css';
import '../styles/quoteBlock.css';
import Quotes from '../quoteGrid/Quotes'
import Gainers from '../quoteGrid/Gainers'
import Mostactive from '../quoteGrid/Mostactive'
import Losers from '../quoteGrid/Losers'
import Positions from '../portfolioGrid/Positions'
import Trades from '../portfolioGrid/Trades'
import Account from '../portfolioGrid/Account'
import SelectBox from '../features/select-box/Tags'
import SelectList from '../features/select-box/ListTen'

export default class QuoteGrid extends Component {
  state = {
    lists:[
      { value:'In Focus', id: 1 },
      { value:'Mostactive', id: 2 },
      { value:'Gainers', id: 3 },
      { value:'Losers', id: 4 },
    ],
    selectedItem: "Account",
    selectedPage: "US Equities",
    selectedList: "In Focus",
    activeTickers: []
  }

  selectList = (list) => {
    this.setState({
      selectedList: list.value,
    })
  }
  selectItem = (item) => {
    this.setState({
      selectedItem: item.value,
    })
    console.log("selected item ---------------------------",this.state.selectedItem)
  }
  selectPage = (page) => {
    this.setState({
      selectedPage: page
    })
  }
  /**************************************************************************/
  addSymbol(symbol) {
    const endpoint = `/api/active/add/${symbol}`
    const promise = apiCall(endpoint,'get')
    promise.then(blob => blob.json()).then(json => {
      if (json.error.length > 0) {
        alert(json.error)
      } else {
        this.setState({activeTickers: json.symbols})
      }
    })
  }
  delSymbol = (symbol) => {
    console.log("deleting",symbol)
    const endpoint = `/api/active/delete/${symbol}`
    const promise = apiCall(endpoint,'get')
    promise.then(blob => blob.json()).then(json => {
    this.setState({activeTickers: json.symbols})
      })
  }
  /***********************************************************************/
  checkList() {
    const endpoint = `/api/active/list`
    const promise = apiCall(endpoint,'get')
    promise.then(blob => blob.json()).then(json => {
      this.setState({
        activeTickers: json.symbols
      })
    })
  }
  componentWillMount() {
    this.checkList();
  }
/************************************************************************/

  render() {
  console.log("QuoteGrid render page-------------", this.state.selectedPage)
  console.log("QuoteGrid render item-------------", this.state.selectedItem)
  let outpTable = (<div></div>)
  if(this.state.selectedPage === "US Equities" && this.state.selectedList === "In Focus") {
    outpTable = (
      <Quotes
        setChartsTicker={this.props.setChartsTicker}
        activeTickers = {this.state.activeTickers}
        delSymbol = {this.delSymbol}
      />
    )
    } else if(this.state.selectedPage === "US Equities" && this.state.selectedList === "Mostactive") {
      outpTable = (
        <Mostactive addSymbol = {this.addSymbol} activeTickers = {this.state.activeTickers}/>
      )
    } else if (this.state.selectedPage === "US Equities" && this.state.selectedList === "Gainers") {
      outpTable = (
        <Gainers addSymbol = {this.addSymbol} activeTickers = {this.state.activeTickers}/>
      )
    } else if (this.state.selectedPage === "US Equities" && this.state.selectedList === "Losers") {
      outpTable = (
        <Losers addSymbol = {this.addSymbol} activeTickers = {this.state.activeTickers}/>
      )
    } else if(this.state.selectedPage === "Portfolio" && this.state.selectItem === "Account") {
      outpTable = (
        <Account/>
      )
    } else if(this.state.selectedPage === "Portfolio" && this.state.selectedItem === "Positions") {
      outpTable = (
        <Positions setChartsTicker={this.props.setChartsTicker}/>
      )
    } else if(this.state.selectedPage === "Portfolio" && this.state.selectedItem === "Trades") {
      outpTable = (
        <Trades setChartsTicker={this.props.setChartsTicker}/>
      )
    }
/************************************************/  
  let addTickerButton = (<div></div>)
  let outputPageOption = (<div></div>)
  if(this.state.selectedPage === "US Equities"){
    addTickerButton = (
      <div className="table-header-left">
        <input className="input-adding"id="symbol"></input>
          <button className = "button-adding" onClick={(event)=>{
            this.addSymbol(document.getElementById('symbol').value)                      
        }}>add symbol</button>
      </div>
    )
    outputPageOption = ( 
      <SelectList
        lists={[
          {from:"US Equities", value:'In Focus', id: 1 },
          {from:"US Equities", value:'Mostactive', id: 2 },
          {from:"US Equities", value:'Gainers', id: 3 },
          {from:"US Equities", value:'Losers', id: 4 },
        ]}
        selectList={this.selectList}
      />
    )    
  } else if(this.state.selectedPage ==="Portfolio"){
    outputPageOption = (
      <SelectBox
        items={[
          {from:"Portfolio", value: 'Account', id: 1 },
          {from:"Portfolio", value: 'Positions', id: 2 },
          {from:"Portfolio", value: 'Trades', id: 3 },
        ]}
        selectItem={this.selectItem}
      />
    )
  }
  let selectFor = (<div></div>)
  if(this.state.selectedPage==="Portfolio" && this.state.selectedItem==="Positions"){
    selectFor = (
    <div style={{display:"flex",flexDirection:"row", justifyContent:"flex-end", alignItems:"center"}}>
        <div>position for : </div>
        <input className="input-adding"id="positionfor"></input>
          <button className = "button-adding" onClick={(event)=>{
            this.addSymbol(document.getElementById('positionfor').value)                      
        }}>show</button>
    </div>
  )
} else if(this.state.selectedPage==="Portfolio" && this.state.selectedItem==="Trades"){
  selectFor = (
    <div style={{display:"flex",flexDirection:"row", justifyContent:"flex-end", alignItems:"center"}}>
        <div>trades for : </div>
        <input className="input-adding"id="tradesfor"></input>
          <button className = "button-adding" onClick={(event)=>{
            this.addSymbol(document.getElementById('tradesfor').value)                      
        }}>show</button>
    </div>
  ) 
} 
    return (
      <Fragment>
        <div className="collar">
                <div><button onClick={()=>{this.selectPage("US Equities")}} className={this.state.selectedPage==="US Equities"?"tag-on":"tag-off"}><p>Quote Grid</p></button></div>
                <div><button onClick={()=>{this.selectPage("Portfolio")}} className={this.state.selectedPage==="Portfolio"?"tag-on":"tag-off"}><p>Portfolio</p></button></div>
                <div className="tag-ext"></div>
              </div>
          <div className="table-header">
          <div className="table-header-left">
            {addTickerButton}
          </div>
            <div className="table-header-center" >
             <div style={{width: "100px",backgroundColor:"red",margin: '0px', position: 'relative'}}>
             {outputPageOption}
            </div>
          </div>
        
          <div className="table-header-right">
          
              {selectFor}
          
          </div>
        </div>
        <div className="split">
          <div className={'scroll-box'}>
            {outpTable}
          </div>
        </div>
      </Fragment>    
    )
  }
}
