import React, { Component, Fragment } from 'react'
import SelectItem from '../../util/SelectItem'
import '../../styles/chart.css'
import AreaChart from './AreaChart'

export default class Summary extends Component {
    state = {
        selfTicker: null
    }

    pickHandle = (symbol) => {
        this.setState({
            selfTicker: symbol
        })
    }
    render() {
        const tickerToLook  = this.props.match.params.symbol
        let tickerDisplayed = (<div></div>)
        if(this.props.lookupTicker){
             tickerDisplayed = (
                <div>
                    {tickerToLook}
                </div>
            )
        }
        return (
            <div className="chart">
                <div className="chart-header">
                    <div>
                        
                    </div>
                    <div  style={{width:"140px", padding:"0.2rem"}}>
                        <SelectItem pickHandle = {this.pickHandle}/>
                    </div>
                </div>
                <div className="chart-field">
                    <div className="chart-info">
                        {tickerDisplayed}
                    </div>
                    <div className="chart-body">
                        <AreaChart lookupTicker={this.props.lookupTicker} selfTicker={this.state.selfTicker}/>
                    </div>

                </div>
            </div>
        )
    }
}
