import React, { Component } from 'react';
import apiCall from '../util/apiCall';
import '../styles/table.css';
import '../styles/style.css';
import PosItem from './PosItem'

export default class Positions extends Component {
  state = {
    investCost: null,
    currentCost: null,
    positionsQty: null,
    sharesQty: null,
    change: null,
    changePrcntg: null,
    positions: [],

    ticker :null,
    positionFor: null,
    error: ""
  }
  
  getPositions() {
    const endpoint = `/api/${window.sessionStorage.getItem("apikey")}/positions`
    const promise = apiCall(endpoint,'get')
      promise.then(blob => blob.json()).then (json=> {
        this.setState({
          positions: json.positions
       })
      })
  }
  getSummary() {
    const endpoint = `/api/${window.sessionStorage.getItem("apikey")}/positions/summary`
    const promise = apiCall(endpoint,'get')
      promise.then(blob => blob.json()).then (json=> {
        this.setState({
          positions: json.positions,
          investCostTotal: json.investCostTotal,
          positionsValueTotal: json.positionsValueTotal,
          positionsQty: json.positionsQty,
          sharesQty: json.sharesQty,
          changeTotal: json.changeTotal,
          changePercentageTotal: json.changePercentageTotal
       })
      })
  }
  getPositionFor(symbol) {
    const endpoint = `/api/${window.sessionStorage.getItem("apikey")}/positions/positionfor/${symbol}`
    const promise = apiCall(endpoint,'get')
      promise.then(blob => blob.json()).then (json=> {
        this.setState({
          positionFor: json.position
       })
      })
  }        
  componentWillMount() {
    this.getSummary()
  }
  componentDidUpdate(prevProps) {
    console.log("Positions componentDidUpdate")
    if(this.props.ticker !== prevProps.ticker) {
      console.log("1prevprops----------",prevProps.ticker)
      console.log("1staterops----------",this.state.ticker)
      this.setState({ticker : this.props.ticker})
      this.getPositionFor(this.props.ticker);
    } 
    else if(this.state.positionfor === null && this.state.ticker || this.state.positionfor === undefined && this.state.ticker) {
      console.log("2prevprops----------",prevProps.ticker)
      console.log("2staterops----------",this.state.ticker)
      this.getPositionFor(this.state.ticker)
    } else {console.log("nothing happened")}
  }
  colored = (value)=> {
    if(value>0) {
      return {backgroundColor:"green",fontSize:"0.9rem",fontWeight:"500",padding:"0px",textAlign:"center"}
     } else if(value<0) {
        return {backgroundColor:"rgb(250, 85, 85)",fontSize:"0.9rem",fontWeight:"500",padding:"0px",textAlign:"center"}
      } else {
        return {color: "yellow",fontSize:"0.9rem",fontWeight:"500",padding:"0px",textAlign:"center"}
      }
    }
  render() {
    console.log("POSITION=========", this.state.positionfor)
    console.log("Positions props_____:", this.props.ticker)
    console.log("Positions state_____:", this.state.ticker)
    const roundTo = require('round-to') 
    let outp = (<div></div>)
    
    if(this.state.positions === [] || this.state.positions === undefined) {
      outp = (<div>NOTHING TO DISPLAY</div>)
    } else {
      outp = (
        <PosItem setChartsTicker={this.props.setChartsTicker} items = {this.state.positions}/>
      )
    }
    return (
      <div className="position-display">
        <div className="position-display-left">
          <table>
            <tr>
              <th><div>Total positions</div></th>
              <td style={styledCell}><div style={styledDiv}>{this.state.positionsQty}</div></td>
            </tr>
            <tr>
              <th>Total shares</th>
              <td style={styledCell}><div style={styledDiv}>{this.state.sharesQty}</div></td>
            </tr>
            <tr>
              <th>Funds Invested</th>
              <td style={styledCell}><div style={styledDiv}>{this.state.investCostTotal}</div></td>
            </tr>
            <tr>
              <th>Portfolio current value</th>
              <td style={styledCell}><div style={styledDiv}>{this.state.positionsValueTotal}</div></td>
            </tr>
            <tr>
              <th>Change</th>
              <td style={styledCell}><div style={this.colored(this.state.changeTotal)}>{this.state.changeTotal}</div></td>
            </tr>
            <tr>
              <th>Change %</th>
              <td style={styledCell}><div style={this.colored(this.state.changeTotal)}>{this.state.changePercentageTotal}</div></td>
            </tr>
          </table>
      
        </div>
        <div className="position-display-right">
          <table>
            <thead>
              <tr>
                <th>Ticker</th>
                <th>Shares</th>
                <th>Invested</th>
                <th>Carrent Value</th>
                <th>Change</th>
                <th>Change %</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {outp}
            </tbody>
          </table>
        </div>
      </div>
    )
   }
}
const styledDiv = {
  textAlign:"center",
  padding:"0.5rem"
}

const styledCell = {
  textAlign:"center",
  padding:"0rem"
}
