import React, { Component, Fragment } from 'react'
import apiCall from '../util/apiCall';
import QuoteSheet from './QuoteSheet'


export default class Quotes extends Component {
    constructor(props) {
        super(props);
        this.state = {
          activeQuotes: [],
          activeTickers : [],
          error: ""
        };
        this.checkList()
      }

    checkList() {
        const endpoint = `/api/${window.sessionStorage.getItem("apikey")}/active/list`
        const promise = apiCall(endpoint,'get')
        promise.then(blob => blob.json()).then(json => {
          this.setState({
            activeTickers: json.symbols
          })
        })
      }

    getQuotes (symbols) {
        console.log("getquotes function")
        const activeTickers = symbols.join()
        const endpoint = `/api/list/${activeTickers}/quote`
        const promise = apiCall(endpoint,'get')
        promise.then(blob => blob.json()).then (json=> {
          this.setState({
            activeQuotes: json.quotes
          })
        })
    }

    addSymbol(symbol) {
        console.log("function addsymbol", symbol)
        const endpoint = `/api/${window.sessionStorage.getItem("apikey")}/active/add/${symbol}`
        const promise = apiCall(endpoint,'get')
        promise.then(blob => blob.json()).then(json => {
          if (json.error.length > 0) {
            alert(json.error)
          } else {
            this.setState({activeTickers: json.symbols})
          }
        })
      }

      delSymbol=(symbol) =>{
        const endpoint = `/api/${window.sessionStorage.getItem("apikey")}/active/delete/${symbol}`
        const promise = apiCall(endpoint,'get')
        promise.then(blob => blob.json()).then(json => {
        this.setState({activeTickers: json.symbols})
          })
      }
    /*
    componentDidUpdate(prevState) {
        if(this.state.activeTickers && this.state.activeTickers.length>0){
          if(!this.state.activeQuotes || this.state.activeQuotes.length===0) {
            this.getQuotes(this.state.activeTickers)
          }
        } 
      }
    */
   shouldComponentUpdate(nextProps, nextState){
    console.log('should component update!',this.state.activeTickers,"++", nextState.activeTickers)
    return this.state.activeTickers!==nextState.activeTickers
   }
  componentWillUpdate(nextProps, nextState) {
    console.log('Component will mount!')
    this.checkList()
    this.getQuotes(this.state.activeTickers)
  }
  componentDidUpdate(prevProps, prevState) {
     console.log('Component did update!')
  }
   
    render() {
      console.log("----------------", this.state.activeTickers)
      console.log("++++++++++++++", this.state.activeQuotes)
          let output = (<Fragment></Fragment>)
          if(this.state.activeQuotes) {
            output=(
            <QuoteSheet
            addSymbol={this.addSymbol}
            delSymbol={this.delSymbol} 
            activeTickers={this.state.activeTickers}
            activeQuotes={this.state.activeQuotes}
            setChartsTicker={this.props.setChartsTicker}
          />
            )
          }
        return (
            <div>
                {output}
            </div>
        )
    }
}
