import React, { Component, Fragment } from 'react'
import SelectItem from '../../util/SelectItem'
import '../../styles/chart.css'
import AreaChart from './AreaChart'

export default class Chart extends Component {
    state = {
        lookupticker: null
    }

    pickHandle = (symbol) => {
        this.setState({
            lookupTicker: symbol
        })
    }
    render() {
        console.log("pickhandle", this.state.lookupTicker)
        let tickerDisplayed = (<div><input></input></div>)
        if(this.props.lookupTicker){
             tickerDisplayed = (
                <div>
                    {this.props.lookupTicker}
                </div>
            )
        }
        return (
            <div className="chart">
                <div className="chart-header">
                    <div>
                        
                    </div>
                    <div  style={{width:"140px", padding:"0.2rem"}}>
                        <SelectItem pickHandle = {this.pickHandle}/>
                    </div>
                </div>
                <div className="chart-field">
                    <div className="chart-info">
                        {tickerDisplayed}
                    </div>
                    <div className="chart-body">
                        <AreaChart lookupTicker={this.props.lookupTicker}/>
                    </div>

                </div>
            </div>
        )
    }
}
