import React, { Component, Fragment } from 'react'
import apiCall from '../util/apiCall';
import '../styles/style.css';
import '../styles/quoteBlock.css';
import Quotes from '../quoteGrid/Quotes'
import Gainers from '../quoteGrid/Gainers'
import Mostactive from '../quoteGrid/Mostactive'
import Losers from '../quoteGrid/Losers'
import Positions from '../portfolioGrid/Positions'
import Trades from '../portfolioGrid/Trades'
import Account from '../portfolioGrid/Account'
import SelectBox from '../features/select-box/Tags'
import SelectPage from '../features/select-box/Pages'
import SelectList from '../features/select-box/ListTen'

export default class QuoteGrid extends Component {
  state = {
    selectedItem: "Account",
    selectedPage: "US Equities",
    selectedList: "In Focus",
    activeTickers: []
  }

  selectList = (list) => {
    this.setState({
      selectedList: list.value,
    })
  }
  selectItem = (item) => {
    this.setState({
      selectedItem: item.value,
    })
  }
  selectPage = (page) => {
    this.setState({
      selectedPage: page
    })
  }
  /**************************************************************************/
  addSymbol(symbol) {
    const endpoint = `/api/active/add/${symbol}`
    const promise = apiCall(endpoint,'get')
    promise.then(blob => blob.json()).then(json => {
      if (json.error.length > 0) {
        alert(json.error)
      } else {
        this.setState({activeTickers: json.symbols})
      }
    })
  }
  delSymbol = (symbol) => {
    console.log("deleting",symbol)
    const endpoint = `/api/active/delete/${symbol}`
    const promise = apiCall(endpoint,'get')
    promise.then(blob => blob.json()).then(json => {
    this.setState({activeTickers: json.symbols})
      })
  }
  /***********************************************************************/
  checkList() {
    const endpoint = `/api/active/list`
    const promise = apiCall(endpoint,'get')
    promise.then(blob => blob.json()).then(json => {
      this.setState({
        activeTickers: json.symbols
      })
    })
  }
  componentWillMount() {
    this.checkList();
  }
/************************************************************************/

  render() {
  console.log("QuoteGrid render-------------", this.state.selectedPage)
  let outpTable = (<div></div>)
  if(this.state.selectedPage === "US Equities") {
    if(this.state.selectedList === "In Focus") {
      outpTable = (
        <Quotes
          setChartsTicker={this.props.setChartsTicker}
          activeTickers = {this.state.activeTickers}
          delSymbol = {this.delSymbol}
        />
      )
    } else if(this.state.selectedList === "Mostactive") {
      outpTable = (
        <Mostactive addSymbol = {this.addSymbol} activeTickers = {this.state.activeTickers}/>
      )
    } else if (this.state.selectedList === "Gainers") {
      outpTable = (
        <Gainers addSymbol = {this.addSymbol} activeTickers = {this.state.activeTickers}/>
      )
    } else if (this.state.selectedList === "Losers") {
      outpTable = (
        <Losers addSymbol = {this.addSymbol} activeTickers = {this.state.activeTickers}/>
      )
    }
  } else if(this.state.selectedPage === "Portfolio") {
    if(this.state.selectItem === "Account") {
      outpTable = (
        <Account/>
      )
    } else if(this.state.selectedItem === "Positions") {
      outpTable = (
        <Positions setChartsTicker={this.props.setChartsTicker}/>
      )
    } else if(this.state.selectedItem === "Trades") {
      outpTable = (
        <Trades setChartsTicker={this.props.setChartsTicker}/>
      )
    }
  }
/************************************************/  
  let outputPage = (<div></div>)
  let outputPageOption = (<div></div>)
  if(this.state.selectedPage === "US Equities"){
    /*outputPage = (

              <SelectPage
                pages={[
                  { value: 'US Equities', id: 1 },
                  { value: 'Portfolio', id: 2 },
                ]}
                selectPage={this.selectPage}
              />
    )*/
    outputPageOption = ( 
              <SelectList
                lists={[
                  { value:'In Focus', id: 1 },
                  { value:'Mostactive', id: 2 },
                  { value:'Gainers', id: 3 },
                  { value:'Losers', id: 4 },
                ]}
                selectList={this.selectList}
              />
    )
     
    } else if(this.state.selectedPage === "Portfolio"){
    /*outputPage = (
                <SelectPage
                pages={[
                  { value: 'US Equities', id: 1 },
                  { value: 'Portfolio', id: 2 },
                ]}
                selectPage={this.selectPage}
              />
    )*/
    outputPageOption = (
              <SelectBox
                items={[
                  { value: 'Account', id: 1 },
                  { value: 'Positions', id: 2 },
                  { value: 'Trades', id: 3 },
                ]}
                selectItem={this.selectItem}
              />
  )
 
  }
    return (
      <Fragment>
        <div className="collar">
                <div onClick={()=>{console.log("click-click-click"), this.selectPage("US Equities")}} className={this.state.selectedPage==="US Equities"?"tag-on":"tag-off"}><p>Quote Grid</p></div>
                <div onClick={()=>{this.selectPage("Portfolio")}} className={this.state.selectedPage==="Portfolio"?"tag-on":"tag-off"}><p>Portfolio</p></div>
                <div className="tag-ext"></div>
              </div>
          <div className="table-header">
            <div className="table-header-left">
              <input className="input-adding"id="symbol"></input>
                <button className = "button-adding" onClick={(event)=>{
                  this.addSymbol(document.getElementById('symbol').value)                      
                }}>add symbol</button>
            </div>
            <div className="table-header-center" >
             <div style={{width: "100px",backgroundColor:"red",margin: '0px', position: 'relative'}}>
              
            </div>
          </div>
        
          <div className="table-header-right">
            <div style={{margin: '0px', position: 'relative'}}>
              {outputPageOption}
            </div>
          </div>
        </div>
        <div className="split">
          <div className={'scroll-box'}>
            {outpTable}
          </div>
        </div>
      </Fragment>    
    )
  }
}
