import React, { Component } from 'react';
import apiCall from '../util/apiCall';
import '../styles/table.css';
import '../styles/style.css';
import PosItem from './PosItem'

export default class Positions extends Component {
  state = {
    
    investCost: null,
    currentCost: null,
    positionsQty: null,
    sharesQty: null,
    change: null,
    changePrcntg: null,
    positions: [],
    error: ""
  }
  
  getPositions() {
    console.log("GET POSITIONS FUNCTION")
    console.log("apikey:",window.sessionStorage.getItem("apikey"))
    const endpoint = `/api/${window.sessionStorage.getItem("apikey")}/positions`
    const promise = apiCall(endpoint,'get')
      promise.then(blob => blob.json()).then (json=> {
        this.setState({
          positions: json.positions
       })
      })
  }
  getSummary() {
    console.log("GET SUMMARY FUNCTION")
    console.log("apikey:",window.sessionStorage.getItem("apikey"))
    const endpoint = `/api/${window.sessionStorage.getItem("apikey")}/positions/summary`
    const promise = apiCall(endpoint,'get')
      promise.then(blob => blob.json()).then (json=> {
        this.setState({
          positions: json.positions,
          investCostTotal: json.investCostTotal,
          currentValueTotal: json.currentValueTotal,
          positionsQty: json.positionsQty,
          sharesQty: json.sharesQty,
          changeTotal: json.changeTotal,
          changePercentageTotal: json.changePercentageTotal
       })
      })
  }        
  componentWillMount() {
    console.log("POSITIONSS WILL- mount START")
    this.getSummary()
    console.log("POSITIONS WILL -mount END")
  }
  colored = (value)=> {
    if(value>0) {
      return {backgroundColor:"green",backgroundColor:"rgb(250, 85, 85)",fontSize:"0.9rem",fontWeight:"500",padding:"0px",textAlign:"center"}
     } else if(value<0) {
        return {backgroundColor:"rgb(250, 85, 85)",fontSize:"0.9rem",fontWeight:"500",padding:"0px",textAlign:"center"}
      } else {
        return {backgroundColor: "yellow",backgroundColor:"rgb(250, 85, 85)",fontSize:"0.9rem",fontWeight:"500",padding:"0px",textAlign:"center"}
      }
    }

  render() {
    console.log("POSITIONSS RENDER START")
    console.log("State Positions", this.state.positions)
    const roundTo = require('round-to') 
    let outp = (<div></div>)
    
    if(this.state.positions === [] || this.state.positions === undefined) {
      outp = (<div>NOTHING TO DISPLAY</div>)
    } else {
      outp = (
        <PosItem setChartsTicker={this.props.setChartsTicker} items = {this.state.positions}/>
      )
    }
    console.log("positions RENDER END")
    return (
      <div className="position-display">
        <div className="position-display-left">
          <table>
            <tr>
              <th>Total positions</th>
              <td style={styledCell}><div>{this.state.positionsQty}</div></td>
            </tr>
            <tr>
              <th>Total shares</th>
              <td style={styledCell}><div style={styledCell}>{this.state.sharesQty}</div></td>
            </tr>
            <tr>
              <th>Funds Invested</th>
              <td style={styledCell}><div style={styledCell}>{this.state.investCostTotal}</div></td>
            </tr>
            <tr>
              <th>Portfolio current value</th>
              <td style={styledCell}><div style={styledCell}>{this.state.currentValueTotal}</div></td>
            </tr>
            <tr>
              <th>Change</th>
              <td style={styledCell}><div style={this.colored(this.state.changeTotal)}>{this.state.changeTotal}</div></td>
            </tr>
            <tr>
              <th>Change %</th>
              <td style={styledCell}><div style={this.colored(this.state.changeTotal)}>{this.state.changePercentageTotal}</div></td>
            </tr>
          </table>
        
          
        </div>
        <div className="position-display-right">
          <table>
            <thead>
              <tr>
                <th>Ticker</th>
                <th>Shares</th>
                <th>Invested</th>
                <th>Carrent Value</th>
                <th>Change</th>
                <th>Change %</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {outp}
            </tbody>
          </table>
        </div>
      </div>
    )
   }
}
const styledCell = {
  textAlign:"center",
  padding:"0.4rem"
}
