import React, {Component} from 'react';
import ReactApexChart from 'react-apexcharts';



class AreaChart extends Component {
   constructor(props) {
    super(props);

    this.state = {
      selection: 'one_month',
      options: {
        annotations: {
        yaxis: [{
          y: 30,
          borderColor: '#999',
          label: {
            show: true,
            text: 'Support',
            style: {
              color: "#fff",
              background: '#00E396'
            }
          }
        }],
        xaxis: [{
          x: new Date('14 Nov 2012').getTime(),
          borderColor: '#999',
          yAxisIndex: 0,
          label: {
            show: true,
            text: 'Rally',
            style: {
              color: "#fff",
              background: '#775DD0'
            }
          }
        }]
      },
      dataLabels: {
        enabled: false
      },
      markers: {
        size: 0,
        style: 'hollow',
      },
      xaxis: {
        type: 'datetime',
        min: 1560902400000,
        // min: new Date('01 Mar 2012').getTime(),
        tickAmount: 6,
      },
      tooltip: {
        x: {
          format: 'dd MMM yyyy'
        }
      },
      fill: {
        type: 'gradient',
        gradient: {
          shadeIntensity: 1,
          opacityFrom: 0.7,
          opacityTo: 0.9,
          stops: [0, 100]
        }
      }
      },
      series: [{
        data: [
          [1560902400000, 10.04],
[1560988800000, 10.04],
[1561075200000, 9.99],
[1561334400000, 9.95],
[1561420800000, 9.84],
[1561507200000, 9.91],
[1561593600000, 10.2],
[1561680000000, 10.23],
[1561939200000, 10.15],
[1562025600000, 10.12],
[1562112000000, 10.2],
[1562284800000, 10.2],
[1562544000000, 10.2],
[1562630400000, 10.14],
[1562716800000, 10.11],
[1562803200000, 10.19],
[1562889600000, 10.49],
[1563148800000, 10.42],
[1563235200000, 10.51],
[1563321600000, 10.33],
[1563408000000, 10.26]
        ]
      }],
    }

  }

  updateData (timeline) {
    this.setState({
      selection: timeline
    })
    
    switch (timeline) {
      case 'one_month':
        this.setState({
          options: {
            xaxis: {
              min: 1560902400000,
              max: 1563408000000,
            }
          }
        })
        break;
      case 'six_months':
        this.setState({
          options: {
            xaxis: {
              min: new Date('27 Sep 2012').getTime(),
              max: new Date('27 Feb 2013').getTime(),
            }
          }
        })
        break;
      case 'one_year':
        this.setState({
          options: {
            xaxis: {
              min: new Date('27 Feb 2012').getTime(),
              max: new Date('27 Feb 2013').getTime(),
            }
          }
        })
        break;
      case 'ytd':
        this.setState({
          options: {
            xaxis: {
              min: new Date('01 Jan 2013').getTime(),
              max: new Date('27 Feb 2013').getTime(),
            }
          }
        })
        break;
      case 'all':
        this.setState({
          options: {
            xaxis: {
              min: undefined,
              max: undefined,
            }
          }
        })
        break;
      default:

    }
  }

  render() {

    return (
      <div>
        <div id="chart">
          <div className="toolbar">
            <button onClick={()=>this.updateData('one_month')} id="one_month" className={ (this.state.selection==='one_month' ? 'active' : '')}>1M</button>
            <button onClick={()=>this.updateData('six_months')} id="six_months" className={ (this.state.selection==='six_months' ? 'active' : '')}>6M</button>
            <button onClick={()=>this.updateData('one_year')} id="one_year" className={ (this.state.selection==='one_year' ? 'active' : '')}>1Y</button>
            <button onClick={()=>this.updateData('ytd')} id="ytd" className={ (this.state.selection==='ytd' ? 'active' : '')}>YTD</button>
            <button onClick={()=>this.updateData('all')} id="all" className={ (this.state.selection==='all' ? 'active' : '')}>ALL</button>
          </div>
          <ReactApexChart options={this.state.options} series={this.state.series} type="area" height="350" />
        </div>
        <div id="html-dist"> 

        </div>
      </div>
    );
  }
}

export default AreaChart;
