import React, { Component } from 'react'
import apiCall from '../util/apiCall';
import Table from './Table'


export default class Quotes extends Component {
    state = {
        activeTickers: null,
        activeQuotes: null
    }


    checkList() {
        const endpoint = `/api/active/list`
        const promise = apiCall(endpoint,'get')
        promise.then(blob => blob.json()).then(json => {
          this.setState({
            activeTickers: json.symbols
          })
        })
      }
      getQuotes (symbols) {
        const activeTickers = symbols.join()
        const endpoint = `/api/list/${activeTickers}/quote`
        const promise = apiCall(endpoint,'get')
        promise.then(blob => blob.json()).then (json=> {
          this.setState({
            activeQuotes: json.quotes
          })
        })
      }
    componentWillMount() {
        this.checkList()
    }
    componentDidMount(){
        if(this.state.activeTickers){
            this.getQuotes(activeTickers)
        }
    }

    render() {
        let dummydata = [
            {
            "symbol": "AAPL",
            "latestPrice": 201.24,
            "marketCap": 925921339200,
            "avgTotalVolume": 24728097,
