            "open": 199.05,
            "close": 201.24,
            "low": 198.81,
            "high": 201.51,
            "week52Low": 142,
            "week52High": 233.47,
            "change": 1.22,
            "changePercent": 0.0061,
            "ytdChange": 0.272691
            },
            {
              "symbol": "TSLA",
              "latestPrice": 199,
              "marketCap": 925921339199,
              "avgTotalVolume": 24728097,
              "peRatio": 16.78,
              "open": 199.05,
              "close": 201.24,
              "low": 198.81,
              "high": 201.51,
              "week52Low": 142,
              "week52High": 233.47,
              "change": 1.22,
              "changePercent": 0.0061,
              "ytdChange": 0.272691
              }
          ]
        return (
            <div>
                <Table data={dummydata}/>
            </div>
        )
    }
}
