                                <div className="chart-info-column-row-value">{open}</div>
                            </div>
                            <div className="chart-info-column-row">
                                <div className="chart-info-column-row-key">Day's Range</div>
                                <div className="chart-info-column-row-value">{low} - {high}</div>
